mapper.insert:
INSERT INTO tb_member (
    mem_id
  , mem_pwd
  , mem_name
  , mem_phone
  , mem_email
  , reg_date
  , mem_ip
  , del_at
) VALUES (
     #{memId}
   , #{memPwd}
   , #{memName}
   , #{memPhone}
   , #{memEmail}
   , SYSDATE
   , #{memIp}
   , 'Y'
)

mapper.update:
UPDATE tb_member
SET
      mem_pwd = #{memPwd}
    , mem_name = #{memName}
    , mem_phone = #{memPhone}
    , mem_email = #{memEmail}
    , reg_date = SYSDATE
    , mem_ip = #{memIp}
    , del_at = #{delAt}
WHERE
    mem_id = #{memId}
